channelMap.put('documentId', msg['documentId']);

// Store the message content in the tmp message
if (msg['messageType'] !== 'ATTACHMENT') { // DOCUMENT and RESONSE: uses the XML as template
	tmp = new XML(msg['messageContent']);
} else { // ATTACHMENT: convert the JSON to XML
	tmp = JsonUtil.toXml(msg['messageContent']);
}

properties.query:
logger.debug('Store CDA Document');

try {
	// Get the Database Connection
	var dbConn = globalChannelMap.get('dbConnection');
	if (!dbConn) { // sanity check
		dbConn = getDBConnection();
		globalChannelMap.put('dbConnection', dbConn);
	}

	// Build the insert script
	var insertString = "INSERT INTO document (document_id, document_date, document_content, location_id, destination_id) " 
		+ "VALUES ('" + $('documentId') + "', '" + $('documentDate') + "', '" + $('documentContent') + "', '" + $('locationId') + "', '" + $('destinationId') + "')";

	// Execute the insertion
	try {
		var result = dbConn.executeUpdate(insertString);
	} catch(e) {
		logger.error('Error saving document "' + $('documentId') + '" into database: ' + e);
		dbConn.rollback();
	}
} catch(err) {
	logger.error('Error on "Store CDA Document": ' + err);
}

