}

com.mirth.connect.plugins.javascriptstep.script:
logger.debug('Get Receivers');

try {
	// CONF-CDXOD023: RCMR_IN000002UV01 SHALL have one or more [1..*] receiver
	var receivers = [];
	for (var i = 0; i < getArrayOrXmlLength(msg['receivers']); i++) {
    		receivers[i] = msg['receivers'][i];
	}

	// store the receivers, necessary to call 'send document service'
	channelMap.put('receivers', receivers);
} catch (e) {
    	var error = 'Error on "Get Receivers": ' + e;
	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

/* Validate the receivers */
var receivers = channelMap.get('receivers');
if (!receivers || receivers.length == 0) {
	storeOBIBError('obib.receiver.error', 'At least one receiver is required to submit document');
}

try {
	// 4.1. Header Attributes

	// CONF-BC0001, CONF-BC0502, CONF-BC0002, CONF-BC0003, CONF-BC0004, CONF-BC0005 (fixed in the message template)
	//default xml namespace = "urn:hl7-org:v3";

	// Check if the loinc is present
	if (!msg['loinc'] || !msg['loinc']['loincCode'] || !msg['loinc']['displayName']) {
		storeOBIBError('obib.loinc.error', 'LOINC is required');
	}

	// ClinicalDocument/templateId
	tmp['templateId'] = buildCDATemplateId(msg['loinc']['loincCode']);

	// CONF-BC0014, CONF-BC0015
	var documentId = UUIDGenerator.getUUID();
	tmp['id'] = buildCDAId("2.16.840.1.113883.3.277.100.3", documentId);
	channelMap.put('documentId', documentId); // store the documentId, necessary in the response

	// CONF-BC0016, CONF-BC0552, CONF-BC0553, CONF-BC0017, CONF-BC0018, CONF-BC0019, CONF-BC0020, CONF-BC0554, CONF-BC0555
	// TODO 'setId' and 'versionNumber' are not implemented

	// ClinicalDocument/code CONF-BC0021, CONF-BC0022
	tmp['code']['@code'] = msg['loinc']['loincCode'];
	tmp['code']['@displayName'] = msg['loinc']['displayName'];

	// ClinicalDocument/title CONF-BC0023, CONF-BC0024
	tmp['title'] = msg['loinc']['displayName'];

	// ClinicalDocument/effectiveTime CONF-BC0025, CONF-BC0026 
	tmp['effectiveTime']['@value'] = formatCDADateTime(new Date());

	// ClinicalDocument/confidentialityCode CONF-BC0027, CONF-BC0028, CONF-BC0503 (fixed in the message template)

	// ClinicalDocument/languageCode CONF-BC0029, CONF-BC0030 (fixed in the message template)
} catch (e) {
	var error = 'Error on "Header Attributes": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try {
	// 4.2.2. Record Target (Patient Identifiers) - Mandatory - 1..1

	var msgPatient = msg['patient']; // inbound object

	// CONF-BC0047, CONF-BC0507, CONF-BC0508 (fixed in the message template)

	var patientRole = tmp['recordTarget']['patientRole'];

	// CONF-BC0048: patientRole SHALL contain at least one or more [1..*] id
	if (getArrayOrXmlLength(msgPatient['ids']) == 0) {
		storeOBIBError('obib.patient.id.error', 'Patient shall contain at least one id');
	}
	// CONF-BC0049, CONF-BC0050
	for (var i = 0; i < getArrayOrXmlLength(msgPatient['ids']); i++) {
		// Insert IDs as first children of 'patientRole'
		patientRole.prependChild(buildCDAId(msgPatient['ids'][i]['type'], 
								      msgPatient['ids'][i]['code']));
	}

	// CONF-BC0051: patientRole MAY contain zero or more [0..*] addr (address)
	for (var i = 0; i < getArrayOrXmlLength(msgPatient['addresses']); i++) {
		patientRole['addr'][i] = buildCDAAddress(msgPatient['addresses'][i]['use'], 
										 msgPatient['addresses'][i]['streetAddress'], 
										 msgPatient['addresses'][i]['city'], 
										 msgPatient['addresses'][i]['province'], 
										 msgPatient['addresses'][i]['postalCode'], 
										 msgPatient['addresses'][i]['contry']);
	}

	// CONF-BC0052: patientRole MAY contain zero or more [0..*] telecom (address)
	for (var i = 0; i < getArrayOrXmlLength(msgPatient['telecoms']); i++) {
		patientRole['telecom'][i] = buildCDATelecom(msgPatient['telecoms'][i]['use'], 
										    msgPatient['telecoms'][i]['type'], 
										    msgPatient['telecoms'][i]['value']);
	}

	// CONF-BC0053, CONF-BC0509
	patientRole['patient'] = new XML('<patient classCode="PSN"/>');

	// CONF-BC0054: patient SHALL contain at least 1 [1..*] name
	if (getArrayOrXmlLength(msgPatient['names']) == 0) {
		storeOBIBError('obib.patient.name.error', 'Patient shall contain at least one name');
	}
	for (var i = 0; i < getArrayOrXmlLength(msgPatient['names']); i++) {  
		patientRole['patient']['name'][i] = buildCDAName(msgPatient['names'][i]['use'], 
											    msgPatient['names'][i]['family'], 
											    msgPatient['names'][i]['given'], 
											    msgPatient['names'][i]['prefix'], 
											    msgPatient['names'][i]['suffix']);
	}

	// CONF-BC0055
	patientRole['patient']['administrativeGenderCode']['@code'] = msgPatient['genderCode'];

	// CONF-BC0056
	patientRole['patient']['birthTime']['@value'] = formatCDADate(msgPatient['birthday']);

	// CONF-BC0057 TODO languageCommunication?
} catch (e) {
	var error = 'Error on "Record Target": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try {
	// 4.2.3. Author - Mandatory - 1..*

	// Update the time for the first author, who is the system author, fixed in the message template
	tmp['author'][0]['time']['@value'] = formatCDADateTime(new Date());

	for (var i = 0; i < getArrayOrXmlLength(msg['authors']); i++) {

		var msgAuthor = msg['authors'][i]; // inbound object

		// CONF-BC0058, CONF-BC0510
	  	var author = new XML('<author typeCode="AUT" contextControlCode="OP" />');

		// CONF-BC0059: author SHALL contain exactly one [1..1] time
	  	if (!msgAuthor['time']) {
			storeOBIBError('obib.author.time.error', 'Author shall contain one time');
		}
		// CONF-BC0060
	  	author['time']['@value'] = formatCDADateTime(msgAuthor['time']);

	  	// CONF-BC0061, CONF-BC0511
	  	author['assignedAuthor']['@classCode'] = 'ASSIGNED';

	  	// CONF-BC0062: assignedAuthor SHALL contain one or more [1..*] id
	  	if (getArrayOrXmlLength(msgAuthor['ids']) == 0) {
			storeOBIBError('obib.author.id.error', 'Author shall contain at least one id');
		}
	  	for (var j = 0; j < getArrayOrXmlLength(msgAuthor['ids']); j++) {
			author['assignedAuthor']['id'][j] = buildCDAId(msgAuthor['ids'][j]['type'], 
										  		  msgAuthor['ids'][j]['code']);
	  	}

	  	// CONF-BC0066: assignedAuthor MAY contain zero or more [0..*] addr (address)
		for (var j = 0; j < getArrayOrXmlLength(msgAuthor['addresses']); j++) {
			author['assignedAuthor']['addr'][i] = buildCDAAddress(msgAuthor['addresses'][j]['use'], 
													    msgAuthor['addresses'][j]['streetAddress'], 
													    msgAuthor['addresses'][j]['city'], 
													    msgAuthor['addresses'][j]['province'], 
													    msgAuthor['addresses'][j]['postalCode'], 
													    msgAuthor['addresses'][j]['contry']);
		}

		// CONF-BC0067: assignedAuthor MAY contain zero or more [0..*] telecom
		for (var j = 0; j < getArrayOrXmlLength(msgAuthor['telecoms']); j++) {
			author['assignedAuthor']['telecom'][i] = buildCDATelecom(msgAuthor['telecoms'][j]['use'], 
														  msgAuthor['telecoms'][j]['type'], 
														  msgAuthor['telecoms'][j]['value']);
		}

		// CONF-BC0512
		var assignedPerson = new XML('<assignedPerson classCode="PSN" determinerCode="INSTANCE"/>');

	  	// CONF-BC0068: assignedPerson SHALL contain exactly one [1..1] name
	  	if (!msgAuthor['name']) {
			storeOBIBError('obib.author.name.error', 'Author shall contain one name');
		}
	  	assignedPerson['name'] = buildCDAName(msgAuthor['name']['use'], 
	  								   msgAuthor['name']['family'], 
	  								   msgAuthor['name']['given'], 
	  								   msgAuthor['name']['prefix'], 
	  								   msgAuthor['name']['suffix']);

		// CONF-BC0065
		author['assignedAuthor']['assignedPerson'] = assignedPerson;

		// Insert authors after the first 'author'
		tmp.insertChildAfter(tmp['author'][0], author);

		// CONF-BC0513, CONF-BC0069 (fixed in the template)
	}
} catch (e) {
	var error = 'Error on "Author": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try {
	// 4.2.6. Custodian - Mandatory - 1..1

	// CONF-BC0082, CONF-BC0083, CONF-BC0084, CONF-BC0514, CONF-BC0515 implemented by the message template

	// CONF-BC0086 If the clinical document is generated from an EMR, this id SHALL NOT contain the root OID of a Health Authority or of the BC Ministry of Health.
	tmp['custodian']['assignedCustodian']['representedCustodianOrganization']['id'] = buildCDAId("2.16.840.1.113883.3.277.100.2", $('locationId'));

	// CONF-BC0087 contain zero or one [0..1] name
	tmp['custodian']['assignedCustodian']['representedCustodianOrganization']['name'] = $('locationName');
} catch (e) {
    	var error = 'Error on "Custodian": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try {
	// 4.2.4. Information Recipient - Optional - 0..*
	for (var i = 0; i < getArrayOrXmlLength(msg['recipients']); i++) {

		var msgRecipient = msg['recipients'][i]; // inbound object

		// CONF-BC0070 (Insert 'informationRecipients' after 'custodian')
		var recipient = createSegmentAfter('informationRecipient', tmp['custodian']);

	  	// CONF-BC0071
	  	// TODO Validate? There SHALL be at least one [1..*] primary recipient (PRCP).
	  	recipient['@typeCode'] = msgRecipient['typeCode'];

	  	// CONF-BC0072, CONF-BC0074
	  	recipient['intendedRecipient'] = new XML('<intendedRecipient classCode="ASSIGNED"/>');

		// CONF-BC0073: intendedRecipient SHOULD contain one to two [1..2] id
		if (getArrayOrXmlLength(msgRecipient['ids']) == 0) {
			storeOBIBError('obib.recipient.id.error', 'Recipient should contain one or two id');
		}
		// CONF-BC0075, CONF-BC0076
		for (var j = 0; j < getArrayOrXmlLength(msgRecipient['ids']); j++) {
			recipient['intendedRecipient']['id'][j] = buildCDAId(msgRecipient['ids'][j]['type'], 
										  		        msgRecipient['ids'][j]['code']);
	  	}

	  	// CONF-BC0077: intendedRecipient MAY contain zero or more [0..*] addr
		for (var j = 0; j < getArrayOrXmlLength(msgRecipient['addresses']); j++) {
			recipient['intendedRecipient']['addr'][i] = buildCDAAddress(msgRecipient['addresses'][j]['use'], 
												       		msgRecipient['addresses'][j]['streetAddress'], 
													       	msgRecipient['addresses'][j]['city'], 
													       	msgRecipient['addresses'][j]['province'], 
													       	msgRecipient['addresses'][j]['postalCode'], 
													       	msgRecipient['addresses'][j]['contry']);
		}

		// CONF-BC0078: intendedRecipient MAY contain zero or more [0..*] telecom
		for (var j = 0; j < getArrayOrXmlLength(msgRecipient['telecoms']); j++) {
			recipient['intendedRecipient']['telecom'][i] = buildCDATelecom(msgRecipient['telecoms'][j]['use'], 
															   msgRecipient['telecoms'][j]['type'], 
															   msgRecipient['telecoms'][j]['value']);
		}

		// CONF-BC0079: intendedRecipient SHALL contain exactly one [1..1] name
		if (!msgRecipient['name']) {
			storeOBIBError('obib.recipient.name.error', 'Recipient shall contain one name');
		}
		var recipientName = new XML('<informationRecipient classCode="PSN" determinerCode="INSTANCE" />');
	  	recipientName['name'] = buildCDAName(msgRecipient['name']['use'], 
	  								  msgRecipient['name']['family'], 
	  								  msgRecipient['name']['given'], 
									  msgRecipient['name']['prefix'], 
	  								  msgRecipient['name']['suffix']);
		recipient['intendedRecipient']['informationRecipient'] = recipientName;

		// CONF-BC0080: intendedRecipient MAY contain zero or one [0..1] receivedOrganization
		if (msgRecipient['receivedOrganization']) {
			var receivedOrganization = new XML('<receivedOrganization />');

			var orgazination = msgRecipient['receivedOrganization'];

			// CONF-BC0545: receivedOrganization SHALL contain at least one [1..*] id
			if (getArrayOrXmlLength(orgazination['ids']) == 0) {
				storeOBIBError('obib.receivedorgazination.id.error', 'Received Organization shall contain at least one id');
			}
			for (var j = 0; j < getArrayOrXmlLength(orgazination['ids']); j++) {
				receivedOrganization['id'][j] = buildCDAId(orgazination['ids'][j]['type'], 
							  		                  orgazination['ids'][j]['code']);
		  	}
		  	// CONF-BC0081: receivedOrganization, SHALL contain exactly one [1..1] name
		  	if (!orgazination['name']) {
				storeOBIBError('obib.receivedorgazination.name.error', 'Received Organization shall contain one name');
			}
			receivedOrganization['name'] = orgazination['name']; // CONF-BC0081
			recipient['intendedRecipient']['receivedOrganization'] = receivedOrganization;
		}
	}
} catch (e) {
	var error = 'Error on "Information Recipient": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try {
	// 4.2.7. Data Enterer - Optional - 0..1	
	// CONF-BC0088: MAY contain zero or one [0..1] dataEnterer
	if (msg['dataEnterer']) {

		var msgDataEnterer = msg['dataEnterer']; // inbound object

		// CONF-BC0516 (Insert 'dataEnterer' before 'component') 
		var dataEnterer = createSegmentBefore('dataEnterer', tmp['component']);
		dataEnterer['@typeCode'] = 'ENT';
		dataEnterer['@contextControlCode'] = 'OP';

		// CONF-BC0090, CONF-BC0517
		dataEnterer['assignedEntity']['@classCode'] = "ASSIGNED";

		// CONF-BC0550: dataEnterer SHALL contain exactly one [1..1] time
		if (!msgDataEnterer['time']) {
			storeOBIBError('obib.dataenterer.time.error', 'Data Enterer shall contain one time');
		}
		dataEnterer['time']['@value'] = formatCDADateTime(msgDataEnterer['time']);

		// CONF-BC0091: assignedEntity SHOULD contain zero or one [0..1] id, which MAY be a local identifier
		if (msgDataEnterer['id']) {
			dataEnterer['assignedEntity']['id'] = buildCDAId(msgDataEnterer['ids'][j]['type'], 
										  		    msgDataEnterer['ids'][j]['code']);
		}

	  	// CONF-BC0092: assignedEntity MAY contain zero or more [0..*] addr elements
		for (var j = 0; j < getArrayOrXmlLength(msgDataEnterer['addresses']); j++) {
			dataEnterer['assignedEntity']['addr'][i] = buildCDAAddress(msgDataEnterer['addresses'][j]['use'], 
													    	    msgDataEnterer['addresses'][j]['streetAddress'], 
													    	    msgDataEnterer['addresses'][j]['city'], 
													    	    msgDataEnterer['addresses'][j]['province'], 
													    	    msgDataEnterer['addresses'][j]['postalCode'], 
													    	    msgDataEnterer['addresses'][j]['contry']);
		}

		// CONF-BC0093: assignedEntity MAY contain zero or more [0..*] telecom elements
		for (var j = 0; j < getArrayOrXmlLength(msgDataEnterer['telecoms']); j++) {
			dataEnterer['assignedEntity']['telecom'][i] = buildCDATelecom(msgDataEnterer['telecoms'][j]['use'], 
														       msgDataEnterer['telecoms'][j]['type'], 
														       msgDataEnterer['telecoms'][j]['value']);
		}

		// CONF-BC0094: assignedEntity SHOULD contain zero or one [0..1]
		if (msgDataEnterer['name']) {
			// CONF-BC0518
			var assignedPerson = new XML('<assignedPerson classCode="PSN" determinerCode="INSTANCE"/>');

	  		// CONF-BC0095
	  		assignedPerson['name'] = buildCDAName(msgDataEnterer['name']['use'], 
	  									   msgDataEnterer['name']['family'], 
	  									   msgDataEnterer['name']['given'], 
	  									   msgDataEnterer['name']['prefix'], 
	  									   msgDataEnterer['name']['suffix']);

			dataEnterer['assignedEntity']['assignedPerson'] = assignedPerson;
		}
	}
} catch (e) {
    	var error = 'Error on "Data Enterer": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try { 
	// 4.2.9. Generic Participant - Optional - 0..*
	for (var i = 0; i < getArrayOrXmlLength(msg['participants']); i++) {

		var msgParticipant = msg['participants'][i]; // inbound object

		// CONF-8504 (Insert 'participant' before 'component') 
		var participant = createSegmentBefore('participant', tmp['component']);

		// CONF-8505
		if (msgParticipant['typeCode']) {
			participant['@typeCode'] = msgParticipant['typeCode'];
		} else {
			participant['@typeCode'] = 'IND';
		}
		participant['@contextControlCode'] = 'OP';

		// CONF-8506
		participant['functionCode'] = new XML('<functionCode code="' + msgParticipant['functionCode'] + '" \
										codeSystem="2.16.840.1.113883.2.20.3.88" \
									     codeSystemName="HL7ParticipationFunction" displayName="Primary Care Physician" />');

		// CONF-8507
		participant['associatedEntity']['@classCode'] = "PROV";

		// CONF-BC0097: associatedEntity SHOULD contain exactly one to two [1..2] id
		if (getArrayOrXmlLength(msgParticipant['ids']) == 0) {
			storeOBIBError('obib.participant.id.error', 'Participant should contain one or two id');
		}
		// CONF-BC0098, CONF-BC0099
		for (var j = 0; j < getArrayOrXmlLength(msgParticipant['ids']); j++) {
			participant['associatedEntity']['id'][j] = buildCDAId(msgParticipant['ids'][j]['type'], 
										  		         msgParticipant['ids'][j]['code']);
	  	}

		// CONF-BC0101: associatedEntity MAY contain zero or more [0..*] addr
		for (var j = 0; j < getArrayOrXmlLength(msgParticipant['addresses']); j++) {
			participant['associatedEntity']['addr'][i] = buildCDAAddress(msgParticipant['addresses'][j]['use'], 
															 msgParticipant['addresses'][j]['streetAddress'], 
															 msgParticipant['addresses'][j]['city'], 
															 msgParticipant['addresses'][j]['province'], 
															 msgParticipant['addresses'][j]['postalCode'], 
															 msgParticipant['addresses'][j]['contry']);
		}

		// CONF-BC0102: associatedEntity MAY contain zero or more [0..*] telecom
		for (var j = 0; j < getArrayOrXmlLength(msgParticipant['telecoms']); j++) {
			participant['associatedEntity']['telecom'][i] = buildCDATelecom(msgParticipant['telecoms'][j]['use'], 
															    msgParticipant['telecoms'][j]['type'], 
															    msgParticipant['telecoms'][j]['value']);
		}

		// CONF-8508, CONF-BC0519
		participant['associatedEntity']['associatedPerson'] = new XML('<associatedPerson classCode="PSN" determinerCode="INSTANCE" />');

		// CONF-BC0100: associatedEntity/associatedPerson SHALL contain exactly one [1..1] name
		if (!msgParticipant['name']) {
			storeOBIBError('obib.participant.name.error', 'Participant shall contain one name');
		}
		participant['associatedEntity']['associatedPerson']['name'] = buildCDAName(msgParticipant['name']['use'], 
																     msgParticipant['name']['family'], 
																     msgParticipant['name']['given'], 
																     msgParticipant['name']['prefix'], 
																     msgParticipant['name']['suffix']);
	}
} catch (e) {
    	var error = 'Error on "Generic Participant": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try { 
	// 4.3.2. Order - Optional - 0..* (zero or more)
	for (var i = 0; i < getArrayOrXmlLength(msg['orders']); i++) {

		var msgOrder = msg['orders'][i];  // inbound object

		// CONF-BC0524 (Insert 'inFulfillmentOf' before 'component') 
		var inFulfillmentOf = createSegmentBefore('inFulfillmentOf', tmp['component']);

		// CONF-BC0524
		inFulfillmentOf['@typeCode'] = 'FLFS'; 

		// CONF-BC0123, CONF-BC0525
		inFulfillmentOf['order'] = new XML('<order classCode="ENC" moodCode="RQO"/>'); 

		// CONF-BC0124: inFulfillmentOf/order SHALL contain at least one [1..*] id
		if (getArrayOrXmlLength(msgOrder['ids']) == 0) {
			storeOBIBError('obib.infulfillmentof.id.error', 'InFulfillmentOf shall contain at least one id');
		}
		for (var j = 0; j < getArrayOrXmlLength(msgOrder['ids']); j++) {
			inFulfillmentOf['order']['id'][j] = buildCDAId(msgOrder['ids'][j]['type'], 
										  		  msgOrder['ids'][j]['code']);
	  	}
		inFulfillmentOf['order']['id'] = buildCDAId(null, msgOrder['id']);

		// CONF-BC0526 TODO ? xpath is quite different from xml
		//if (msgOrder['statusCode']) {
			// XPath: /ClinicalDocument/inFulfillmentOf/order/statusCode/@code
			// XML: <code code="NW" codeSystemName="Order Status (Order Control[:Order Status][:Response Flag])" />
		//}
	}
} catch (e) {
    	var error = 'Error on "Order (InFulfillmentOf)": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try { 
	// 2.3. CDA LEVEL 1 NARRATIVE TEXT AND ATTACHMENTS

	var msgNonXMLBody = msg['nonXMLBody']; // inbound object

	// CONF-BC0009, CONF-BC0010
	if (msg['nonXMLBody']['mediaType'] === "text/plain") {
		// CONF-BC0011: text.mediaType=text/plain
		tmp['component']['nonXMLBody']['text'] = new XML('<text mediaType="' + msgNonXMLBody['mediaType'] + '" \
			representation="TXT">' + msgNonXMLBody['content'] + '</text>');
	} else { 
		// CONF-BC0012: text.mediaType=text/rtf, or application/pdf
		tmp['component']['nonXMLBody']['text'] = new XML('<text mediaType="' + msgNonXMLBody['mediaType'] + '"/>');
		tmp['component']['nonXMLBody']['text']['reference'] = new XML('<reference value="' + msgNonXMLBody['content'] + '"/>');
	}
} catch (e) {
    	var error = 'Error on "Document Body": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try {
	var attachments = [];
	for (var i = 0; i < getArrayOrXmlLength(msg['attachments']); i++) {
		attachments.push(msg['attachments'][i]); // inbound object
	}

	if (attachments.length > 0) { // store the attachments in the channel map to be send by the connector
		logger.debug('Attachments being submitted: ' + attachments.length);
		channelMap.put('attachments', attachments);
	}
} catch (e) {
    	var error = 'Error on "Attachments": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

logger.debug("Document Validation");

/* Validate the CDA using the XML Schemas */
try {
	// build the XML with correct namespaces
	default xml namespace = "urn:hl7-org:v3";
	var msgValidate = new XML(tmp.toString());
	msgValidate.addNamespace(new Namespace('xsi','http://www.w3.org/2001/XMLSchema-instance'));

	// get the validator using the xml schemas
	var factory = new Packages.javax.xml.validation.SchemaFactory.newInstance('http://www.w3.org/2001/XMLSchema');
	var schemaLocation = new Packages.java.io.File($('custom-lib') + 'CDA_with_BcCdaExtensions/' + globalMap.get('CDASchema'));
	var schema = factory.newSchema(schemaLocation);
	var validator = schema.newValidator();

	// transform the xml into a stream
	var reader = new Packages.java.io.StringReader(msgValidate);
	var source = new Packages.javax.xml.transform.stream.StreamSource(reader);

	// validate the stream
	try {
		validator.validate(source);
	} catch(e) {
		var error = 'CDA XML validation errors: ' + e.message.replace('org.xml.sax.SAXParseException:','').replace(/("|')/g, '');
		logger.error(error); // log the error
    		// storeOBIBError('obib.cda.validation.error', 'CDA XML validation errors: ' + error);
	}
} catch(e) {
	var error = 'Error on "Document Validation": ' + e;
	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

// check for errors in the cda building
var obibErrors = channelMap.get('obibErrors');
if (obibErrors != null) {
	var error = 'Errors building CDA';

	responseMap.put('responseMessage', createErrorMessage(error, obibErrors)); // create the response
	responseMap.put('responseStatus', '500');

	throw error + ': ' + JSON.stringify(obibErrors); // stop the processing
}

logger.debug('Submit Document Response');

try {
	// Store the response to be returned by the "Source Connector"
	responseMap.put('responseMessage', handleResponseMessage(responseStatus, responseStatusMessage, msg, function() {
		// Ack details
		tmp['ackDetail']['ackId'] = msg['id']['@extension'].toString();
		tmp['ackDetail']['ackTime'] = parseCDADateTime(msg['creationTime']['@value'].toString());
		tmp['ackDetail']['messageId'] = msg['acknowledgement']['targetMessage']['id']['@extension'].toString();
		tmp['ackDetail']['status'] = msg['acknowledgement']['acknowledgementDetail']['@typeCode'].toString();

		// Payload
        	tmp['documentId'] = channelMap.get('documentId');

        	// Process the CDA Document in 'CDA Document Parser' channel, and return it in the response
        	var chResponse = router.routeMessage("CDA Document Parser", new XML(channelMap.get('document')));
		if (chResponse.status === SENT) {
			tmp['document'] = JSON.parse(chResponse.message);
			tmp['document']['cdaXML'] = channelMap.get('document');

			var attachments = channelMap.get('attachments');
			if (attachments !== null) {
				tmp['document']['attachments'] = attachments;
			}
		} else {
			logger.error('Error parsing CDA Document ' + chResponse.statusMessage);
		}

		// Message status
		if (tmp['ackDetail']['status'] === 'E') {
			tmp['message'] = msg['acknowledgement']['acknowledgementDetail']['text'].toString();
			tmp['status'] = "ERROR";

			logger.error('MessagId ' + tmp['ackDetail']['messageId'] + ' returned the error: ' + tmp['message'] + "'");
		} else {
			tmp['status'] = "SUCCESS";

			logger.debug('MessageId ' + tmp['ackDetail']['messageId'] + ' was successfully sent.');
		}

		return tmp; // Return the transformed message to be converted on 'handleResponseMessage'
	}));
	responseMap.put('responseStatus', '200');
} catch(e) {
	logger.error("Error on 'Submit Document Response': " + e);

	responseMap.put('responseMessage', createErrorMessage(e));
	responseMap.put('responseStatus', '500');
}

properties.script:
