	return ResponseFactory.getErrorResponse("Error on 'Service List New Documents': " + e)
}

com.mirth.connect.plugins.javascriptstep.script:
logger.debug('List New Documents Response');

try {
	// Store the response to be returned by the "Source Connector"
	responseMap.put('responseMessage', handleResponseMessage(responseStatus, responseStatusMessage, msg, function() {
		// Ack details
		tmp['ackDetail']['ackId'] = msg['id']['@extension'].toString();
		tmp['ackDetail']['ackTime'] = parseCDADateTime(msg['creationTime']['@value'].toString());
		tmp['ackDetail']['messageId'] = msg['acknowledgement']['targetMessage']['id']['@extension'].toString();
		tmp['ackDetail']['status'] = msg['controlActProcess']['queryAck']['queryResponseCode']['@code'].toString();

		// Payload
		for (var i = 0; i < getArrayOrXmlLength(msg['controlActProcess']['subject']); i++) { // Document list
			var target = msg['controlActProcess']['subject'][i]['target'];
			var document = {};

			document['documentId'] = target['id']['@extension'].toString();

			document['template'] = {};
			document['template']['templateId'] = target['templateId']['@extension'].toString();
			document['template']['templateName'] = target['templateId']['@assigningAuthorityName'].toString();

			document['loinc'] = {};
			document['loinc']['loincCode'] = target['code']['@code'].toString();
			document['loinc']['displayName'] = target['code']['@displayName'].toString();

			document['effectiveTime'] = parseCDADateTime(target['effectiveTime']['@value'].toString());

			tmp['documents'][i] = document;
		}

		// Message status
		if (tmp['ackDetail']['status'] === 'QE') {
			tmp['message'] = msg['acknowledgement']['acknowledgementDetail']['text'].toString();
			tmp['status'] = "ERROR";

			logger.error('MessagId ' + tmp['ackDetail']['messageId'] + ' returned the error: ' + tmp['message'] + "'");
		} else {
			tmp['status'] = "SUCCESS";
		}

		return tmp; // Return the transformed message to be converted on 'handleResponseMessage'
	}));
	responseMap.put('responseStatus', '200');
} catch(e) {
	logger.error("Error on 'List New Documents Response': " + e);

	responseMap.put('responseMessage', createErrorMessage(e));
	responseMap.put('responseStatus', '500');
}

