try {
	// 4.2.2. Record Target (Patient Identifiers) - Mandatory - 1..1

	// CONF-BC0047, CONF-BC0507, CONF-BC0508 (fixed in the message template)

	var patientRole = tmp['recordTarget']['patientRole'];

	// CONF-BC0048, CONF-BC0049, CONF-BC0050 TODO local or out of province PHN?
	for (var i = 0; i < getArrayOrXmlLength(msg['patient']['id']); i++) {
		// Insert IDs as first children of 'patientRole'
		patientRole.prependChild(buildCDAId(msg['patient']['id'][i]['type'], 
								      msg['patient']['id'][i]['id']));
	}

	// CONF-BC0051: patientRole MAY contain zero or more [0..*] addr (address)
	for (var i = 0; i < getArrayOrXmlLength(msg['patient']['address']); i++) {
		patientRole['addr'][i] = buildCDAAddress(msg['patient']['address'][i]['use'], 
										 msg['patient']['address'][i]['streetAddress'], 
										 msg['patient']['address'][i]['city'], 
										 msg['patient']['address'][i]['province'], 
										 msg['patient']['address'][i]['postalCode'], 
										 msg['patient']['address'][i]['contry']);
	}

	// CONF-BC0052: patientRole MAY contain zero or more [0..*] telecom (address)
	for (var i = 0; i < getArrayOrXmlLength(msg['patient']['telecom']); i++) {
		patientRole['telecom'][i] = buildCDATelecom(msg['patient']['telecom'][i]['use'], 
										    msg['patient']['telecom'][i]['type'], 
										    msg['patient']['telecom'][i]['value']);
	}

	// CONF-BC0053, CONF-BC0509
	patientRole['patient'] = new XML('<patient classCode="PSN"/>');

	// CONF-BC0054: patient SHALL contain at least 1 [1..*] name
	for (var i = 0; i < getArrayOrXmlLength(msg['patient']['name']); i++) {  
	  patientRole['patient']['name'][i] = buildCDAName(msg['patient']['name'][i]['use'], 
											 msg['patient']['name'][i]['family'], 
											 msg['patient']['name'][i]['given'], 
											 msg['patient']['name'][0]['prefix'], 
											 msg['patient']['name'][0]['suffix']);
	}

	// CONF-BC0055
	patientRole['patient']['administrativeGenderCode']['@code'] = msg['patient']['gender'];

	// CONF-BC0056
	patientRole['patient']['birthTime']['@value'] = formatCDAdataTime(msg['patient']['dob']);

	// CONF-BC0057 TODO languageCommunication?
} catch (e) {
    logger.error('Error on Transformer "Record Target": ' + e);
}

