	// 2.3. CDA LEVEL 1 NARRATIVE TEXT AND ATTACHMENTS

	// CONF-BC0009, CONF-BC0010
	// CONF-BC0011: text.mediaType=”text/plain”
	if (msg['nonXMLBody']['mediaType'] === "text/plain") {
		tmp['component']['nonXMLBody']['text'] = new XML('<text mediaType="' + msg['nonXMLBody']['mediaType'] + '" \
			representation="TXT">' + msg['nonXMLBody']['content'] + '</text>');
	} else { // CONF-BC0012: text.mediaType=”text/rtf”, or “application/pdf”
		tmp['component']['nonXMLBody']['text'] = new XML('<text mediaType="' + msg['nonXMLBody']['mediaType'] + '" \
			value="' + msg['nonXMLBody']['content'] + '"/>');
	}
} catch (e) {
    logger.error('Error on Transformer "Document Body": ' + e);
