importPackage(Packages.ca.uvic.leadlab.cdxconnector);
importPackage(Packages.ca.uvic.leadlab.cdxconnector.messages);
logger.debug('Service Search Documents');
try {
	var client = new WSClientDocument($('cdxBaseUrl'), $('username'), $('password'), $('certPath'), $('certPass'));

	var queryParameter = new DocumentQueryParameterBuilder().documentType(DocumentQueryParameterBuilder.DocumentType.CDA);
	if ($('effectiveTime_start') && $('effectiveTime_end')) {
		queryParameter.documentEffectiveTime($('effectiveTime_start'), true, $('effectiveTime_end'), true);
	}
	if ($('eventTime_start') && $('eventTime_end')) {
		queryParameter.eventEffectiveTime($('eventTime_start'), true, $('eventTime_end'), true)
	}
	if ($('clinicId')) {
		queryParameter.clinicId($('clinicId'))
	}
	if ($('documentId')) {
		queryParameter.documentId($('documentId'));
