try {
	// Document Header
	tmp['templateId'] = msg['templateId']['@root'].toString();
	tmp['id'] = msg['id']['@extension'].toString();
	tmp['code'] = msg['code']['@code'].toString();
	tmp['effectiveTime'] = msg['effectiveTime']['@value'].toString();

	// RecordTarget (Patient)
	var patientRole = msg['recordTarget']['patientRole'];

	var patient = {};

	patient['id'] = [];
	for (var i = 0; i < getArrayOrXmlLength(patientRole['id']); i++) {
		patient['id'][i] = parseCDAId(patientRole['id'][i]);
	}

	if (patientRole['addr']) {
		patient['address'] = parseCDAAddress(patientRole['addr']);
	}

	if (patientRole['telecom']) {
		patient['telecom'] = [];
		for (var i = 0; i < getArrayOrXmlLength(patientRole['telecom']); i++) {
			patient['telecom'][i] = parseCDATelecom(patientRole['telecom'][i]);
		}
	}

	patient['name'] = [];
	for (var i = 0; i < getArrayOrXmlLength(patientRole['patient']['name']); i++) {
		patient['name'][i] = parseCDAName(patientRole['patient']['name'][i]);
	}

	patient['gender'] = patientRole['patient']['administrativeGenderCode']['@code'].toString()

	patient['dob'] = patientRole['patient']['birthTime']['@value'].toString()

	tmp['patient'] = patient;
} catch(e) {
	logger.error("Error on 'Parse RecordTarget (Patient)': " + e);
}

try {
	// Authors
	for (var i = 0; i < getArrayOrXmlLength(msg['author']); i++) {
		var author = {};

		author['time'] = msg['author'][i]['time']['@value'].toString();

		var assignedAuthor = msg['author'][i]['assignedAuthor'];

		author['id'] = [];
		for (var j = 0; j < getArrayOrXmlLength(assignedAuthor['id']); j++) {
			author['id'][j] = parseCDAId(assignedAuthor['id'][j]);
