				author['telecom'][j] = parseCDATelecom(assignedAuthor['telecom'][j]);
			}
		}

		if (assignedAuthor['assignedAuthoringDevice']) {
			var softwareName = assignedAuthor['assignedAuthoringDevice']['softwareName'];
			author['software'] = {}
			author['software']['name'] = softwareName['@displayName'].toString();
			author['software']['code'] = softwareName['@code'].toString();
		} else {
			author['name'] = parseCDAName(assignedAuthor['assignedPerson']['name']);
		}

		tmp['author'][i] = author;
	}
} catch(e) {
	logger.error("Error on 'Parse Authors': " + e);
}

