// This script executes once for each deploy or redeploy task
// You only have access to the globalMap here to persist data
logger.debug("Global Scripts: Deploy");

/*********** Verify global variables ***********/
if (!new Packages.java.io.File($('custom-lib')).exists()) {
	logger.error('Global Scripts: "custom-lib" is not properly configurated on "Configuration Map".');
}

globalMap.put('CDASchema', 'CDA.xsd');
if (!new Packages.java.io.File($('custom-lib') + 'CDA_with_BcCdaExtensions/' + globalMap.get('CDASchema')).exists()) {
	logger.error('Global Scripts: "CDA.xsd" schema cannot be found, channels will not work correctly.');
}

globalMap.put('CDAProperties', 'CDA.properties');
if (!new Packages.java.io.File($('custom-lib') + globalMap.get('CDAProperties')).exists()) {
	logger.error('Global Scripts: "CDA.properties" cannot be found, channels will not work correctly.');
}


/*********** Load CDA properties ***********/
var properties = new Packages.java.util.Properties();
var fileProp = new Packages.java.io.FileInputStream(new Packages.java.io.File($('custom-lib') + globalMap.get('CDAProperties')));

properties.load(fileProp);
globalMap.globalVariableMap.putAll(properties);
fileProp.close();
logger.debug("Global Scripts: Loaded '" + properties.size() + "' CDA properties.");


/*********** Load CDX Ids from database ***********/
try {
	dbConn = getDBConnection();

	var count = 0;
	var result = dbConn.executeCachedQuery('SELECT * FROM cdx_id;');
	while (result && result.next()) {
		globalMap.put(result.getString('extension'), result.getString('name'));
		count++;
	}
	result.close();
	logger.debug("Global Scripts: Loaded '" + count + "' CDX ids.");

	dbConn.close();
} catch(err) {
	logger.error('Global Scripts: Erro connecting to database. ' + err);
}

return;

// Modify the message variable below to pre process data
// This script applies across all channels
return message;
