var clinicId = sourceMap.get('headers').getHeader('clinicId');

try {
	// Get the credentials by clinic_id
	result = executeCachedQuery("SELECT clinic_password FROM clinic_credential WHERE clinic_id = '" + clinicId + "'");
	while (result && result.next()) {
		// Validate the password
		if (result.getString('clinic_password') == sourceMap.get('headers').getHeader('password')) {			
			logger.info("Clinic '" + clinicId + "' authenticated.");
			return AuthenticationResult.Success();
		}
	}
} catch (e) {
	logger.error("Error on 'Authentication Script': " + e);
} finally {
	if (result != null) {
		Packages.org.apache.commons.dbutils.DbUtils.closeQuietly(result);
	}
}

logger.warn("Failed to authenticate clinic '" + clinicId + "'");
return AuthenticationResult.Failure();

