	channelMap.put('document', document.toString());

	// Send the document to "Document Storage" channel
	var storeDoc = buildDocumentStorageMessage($('locationId'), $('documentId'), 'DOCUMENT', document.toString(), $('connectorVersion'));
	router.routeMessage("Document Storage", JSON.stringify(storeDoc));

	// Get the attachments, if exist any
	var attachments = channelMap.get('attachments');
	/*if (attachments !== null && attachments.length > 0) {
		logger.debug('Service Submit Document - Handling Attachments');
		// TODO Save attachment - Transformer error ERROR MESSAGE: Error evaluating transformer
		for (var i = 0; i < attachments.length; i++) {
			var storeAttach = buildDocumentStorageMessage($('locationId'), $('documentId'), 'ATTACHMENT', attachments[i], $('connectorVersion'));
			router.routeMessage("Document Storage", JSON.stringify(storeAttach));
		}
	}*/

	// Call the WS method using CDX Connector
	var cdxResponse = client.submitDocument($('locationId'), document, createDocumentAttachmentArray(attachments), $('receivers'));

	// Send the response to "Document Storage" channel
	var storeResp = buildDocumentStorageMessage($('locationId'), $('documentId'), 'RESPONSE', cdxResponse.toString(), $('connectorVersion'));
	router.routeMessage("Document Storage", JSON.stringify(storeResp));

	// return the raw response to be processed by the 'response transformers'
	return ResponseFactory.getSentResponse(cdxResponse);
} catch (e) {
	logger.error("Error on 'Service Submit Document': " + e);

	responseMap.put('responseMessage', createErrorMessage("Error on 'Service Submit Document': " + e.message, null)); // create the error response
	responseMap.put('responseStatus', '200'); // return ok
}

com.mirth.connect.plugins.javascriptstep.script:
logger.debug('Get Receivers');

try {
	// CONF-CDXOD023: RCMR_IN000002UV01 SHALL have one or more [1..*] receiver
	var receivers = [];
	for (var i = 0; i < getArrayOrXmlLength(msg['receivers']); i++) {
    		receivers[i] = msg['receivers'][i];
	}

	// store the receivers, necessary to call 'send document service'
	channelMap.put('receivers', receivers);
} catch (e) {
    	var error = 'Error on "Get Receivers": ' + e;
	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

/* Validate the receivers */
var receivers = channelMap.get('receivers');
if (!receivers || receivers.length == 0) {
	storeOBIBError('obib.receiver.error', 'At least one receiver is required to submit document');
}

try {
	// 4.1. Header Attributes

	var msgLoinc = msg['loinc']; // inbound object

	// CONF-BC0001, CONF-BC0502, CONF-BC0002, CONF-BC0003, CONF-BC0004, CONF-BC0005 (fixed in the message template)
	//default xml namespace = "urn:hl7-org:v3";

	// Check if the loinc is present
	if (!msgLoinc || !msgLoinc['loincCode'] || !msgLoinc['displayName']) {
		storeOBIBError('obib.loinc.error', 'LOINC is required');
	}

	// ClinicalDocument/templateId
	if (msgLoinc) {
		tmp['templateId'] = buildCDATemplateId(msgLoinc['loincCode']);
	}

	var isNew = true; // verify if the document is new or not
	if (msg['documentId']) {
		var parentDocumentId = buildCDAId("2.16.840.1.113883.3.277.100.3", msg['documentId']);
		channelMap.put('parentDocumentId', parentDocumentId); // store the parentDocumentId, used in RelatedDocument
		isNew = false;
	}

	// CONF-BC0014, CONF-BC0015
	var documentId = UUIDGenerator.getUUID();
	tmp['id'] = buildCDAId("2.16.840.1.113883.3.277.100.3", documentId);
	channelMap.put('documentId', documentId); // store the documentId, necessary in the response

	// CONF-BC0016, CONF-BC0552, CONF-BC0553, CONF-BC0017, CONF-BC0018, CONF-BC0019, CONF-BC0020, CONF-BC0554, CONF-BC0555
	var versionNumber = createSegmentAfter('versionNumber', tmp['languageCode']);
	var setId = createSegmentAfter('setId', tmp['languageCode']);
	if (isNew) { // for new documents, set versionNumber to 1 and copy documentId into setId
		versionNumber['@value'] = 1;

		tmp['setId']['@root'] = tmp['id']['@root'];
		tmp['setId']['@extension'] = tmp['id']['@extension'];
		tmp['setId']['@assigningAuthorityName'] = tmp['id']['@assigningAuthorityName'];
	} else { // for updated documents, increment versionNumber and copy parentDocumentId into setId
		versionNumber['@value'] = msg['versionNumber'] + 1;

		var parentDocumentId = $('parentDocumentId');
		tmp['setId']['@root'] = parentDocumentId['@root'];
		tmp['setId']['@extension'] = parentDocumentId['@extension'];
		tmp['setId']['@assigningAuthorityName'] = parentDocumentId['@assigningAuthorityName'];
	}

	if (msgLoinc) {
		// ClinicalDocument/code CONF-BC0021, CONF-BC0022
		tmp['code']['@code'] = msgLoinc['loincCode'];
		tmp['code']['@displayName'] = msgLoinc['displayName'];

		// ClinicalDocument/title CONF-BC0023, CONF-BC0024
		tmp['title'] = msgLoinc['displayName'];
	}

	// ClinicalDocument/effectiveTime CONF-BC0025, CONF-BC0026 
	var effectiveTime = msg['effectiveTime'];
	if (!effectiveTime) {
		effectiveTime = new Date();
	}
	tmp['effectiveTime']['@value'] = formatCDADateTime(effectiveTime);

	// ClinicalDocument/confidentialityCode CONF-BC0027, CONF-BC0028, CONF-BC0503 (fixed in the message template)

	// ClinicalDocument/languageCode CONF-BC0029, CONF-BC0030 (fixed in the message template)
} catch (e) {
	var error = 'Error on "Header Attributes": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try {
	// 4.2.2. Record Target (Patient Identifiers) - Mandatory - 1..1

	var msgPatient = msg['patient']; // inbound object

	// CONF-BC0047, CONF-BC0507, CONF-BC0508 (fixed in the message template)

	var patientRole = tmp['recordTarget']['patientRole'];

	// CONF-BC0048: patientRole SHALL contain at least one or more [1..*] id
	if (getArrayOrXmlLength(msgPatient['ids']) == 0) {
		storeOBIBError('obib.patient.id.error', 'Patient shall contain at least one id');
	}
	// CONF-BC0049, CONF-BC0050
	for (var i = 0; i < getArrayOrXmlLength(msgPatient['ids']); i++) {
		// Insert IDs as first children of 'patientRole'
		patientRole.prependChild(buildCDAId(msgPatient['ids'][i]['type'], 
								      msgPatient['ids'][i]['code']));
	}

	// CONF-BC0051: patientRole MAY contain zero or more [0..*] addr (address)
	for (var i = 0; i < getArrayOrXmlLength(msgPatient['addresses']); i++) {
		patientRole['addr'][i] = buildCDAAddress(msgPatient['addresses'][i]['use'], 
										 msgPatient['addresses'][i]['streetAddress'], 
										 msgPatient['addresses'][i]['city'], 
										 msgPatient['addresses'][i]['province'], 
										 msgPatient['addresses'][i]['postalCode'], 
										 msgPatient['addresses'][i]['contry']);
	}

	// CONF-BC0052: patientRole MAY contain zero or more [0..*] telecom (address)
	for (var i = 0; i < getArrayOrXmlLength(msgPatient['telecoms']); i++) {
		patientRole['telecom'][i] = buildCDATelecom(msgPatient['telecoms'][i]['use'], 
										    msgPatient['telecoms'][i]['type'], 
										    msgPatient['telecoms'][i]['value']);
	}

	// CONF-BC0053, CONF-BC0509
	patientRole['patient'] = new XML('<patient classCode="PSN" determinerCode="INSTANCE"/>');

	// CONF-BC0054: patient SHALL contain at least 1 [1..*] name
	if (getArrayOrXmlLength(msgPatient['names']) == 0) {
		storeOBIBError('obib.patient.name.error', 'Patient shall contain at least one name');
	}
	for (var i = 0; i < getArrayOrXmlLength(msgPatient['names']); i++) {  
		patientRole['patient']['name'][i] = buildCDAName(msgPatient['names'][i]['use'], 
											    msgPatient['names'][i]['family'], 
											    msgPatient['names'][i]['given'], 
											    msgPatient['names'][i]['prefix'], 
											    msgPatient['names'][i]['suffix']);
	}

	// CONF-BC0055
	patientRole['patient']['administrativeGenderCode']['@code'] = msgPatient['genderCode'];

	// CONF-BC0056
	patientRole['patient']['birthTime']['@value'] = formatCDADate(msgPatient['birthday']);

	// CONF-BC0057 TODO languageCommunication?
} catch (e) {
	var error = 'Error on "Record Target": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

