		participant['associatedEntity']['associatedPerson'] = new XML('<associatedPerson classCode="PSN" determinerCode="INSTANCE" />');

		// CONF-BC0100: associatedEntity/associatedPerson SHALL contain exactly one [1..1] name
		if (!msgParticipant['name']) {
