try { 
	// 4.3.2. Order - Optional - 0..* (zero or more)
	for (var i = 0; i < getArrayOrXmlLength(msg['orders']); i++) {

		var msgOrder = msg['orders'][i];  // inbound object

		// Insert 'inFulfillmentOf' before 'component'
		var inFulfillmentOf = createSegmentBefore('inFulfillmentOf', tmp['component']);

		// CONF-BC0524
		inFulfillmentOf['@typeCode'] = 'FLFS'; 

		// CONF-BC0123, CONF-BC0525
		inFulfillmentOf['order'] = new XML('<order classCode="ENC" moodCode="RQO"/>'); 

		// CONF-BC0124: inFulfillmentOf/order SHALL contain at least one [1..*] id
		if (getArrayOrXmlLength(msgOrder['ids']) == 0) {
			storeOBIBError('obib.infulfillmentof.id.error', 'InFulfillmentOf shall contain at least one id');
		}
		for (var j = 0; j < getArrayOrXmlLength(msgOrder['ids']); j++) {
			var oscarOID = UUIDGenerator.getUUID(); // TODO using a random code for now
			inFulfillmentOf['order']['id'][j] = buildCDAId(oscarOID, msgOrder['ids'][j]['code']);
	  	}

		// CONF-BC0526
		if (msgOrder['statusCode'] != null) {
			var code = new XML('<code codeSystem="statusCode"/>'); 
			code['@code'] = msgOrder['statusCode'];
			inFulfillmentOf['order']['code'] = code;
		}
	}
} catch (e) {
    	var error = 'Error on "Order (InFulfillmentOf)": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try { 
	// 4.3.1. Service Event - Optional - 0..* (zero or more)
	for (var i = 0; i < getArrayOrXmlLength(msg['serviceEvents']); i++) {

		var msgService = msg['serviceEvents'][i];  // inbound object

		// Insert 'documentationOf' before 'component'
		var documentationOf = createSegmentBefore('documentationOf', tmp['component']);

		// CONF-BC0520
		documentationOf['@typeCode'] = 'DOC'; 

		// CONF-BC0103, CONF-BC0521, CONF-BC0547
		documentationOf['serviceEvent'] = new XML('<serviceEvent classCode="ACT" moodCode="EVN"/>'); 

		// CONF-BC00549 TODO ServiceEvent/code

		// 4.1.8. documentationOf/serviceEvent/effectiveTime
		// CONF-BC0548: ServiceEvent SHALL contain exactly one [1..1] effectiveTime
		if (!msgService['effectiveTime']) {
			storeOBIBError('obib.serviceEvents.effectiveTime.error', 'ServiceEvent shall contain one effectiveTime');
		}
		documentationOf['serviceEvent']['effectiveTime']['@value'] = formatCDADateTime(msgService['effectiveTime']);

		// TODO Performer:
		// CONF-BC0104, CONF-BC0105, CONF-BC0106, CONF-BC0522, CONF-BC0107, CONF-BC0108, CONF-BC0109, CONF-BC0110
		// CONF-BC0523, CONF-BC0111, CONF-BC0112, CONF-BC0113

		// 4.1.7. documentationOf/serviceEvent/statusCode
		var statusCode = new XML('<bccda:statusCode xmlns:bccda="urn:bccda" />'); 
		statusCode['@code'] = msgService['statusCode'];
		documentationOf['serviceEvent'].*::['statusCode'] = statusCode;
	}
} catch (e) {
    	var error = 'Error on "ServiceEvent (documentationOf)": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try { 
	// 4.3.3. relatedDocument - Optional - 0..2 (at most 2)	
	// TODO CONF-BC0125
	if ($('parentDocumentId')) {
		// CONF-BC0126, CONF-BC0559, CONF-BC0560
		var relatedDocument = createSegmentBefore('relatedDocument', tmp['component']);
		relatedDocument['@typeCode'] = 'RPLC'; // XFRM

    		// CONF-BC0127
    		relatedDocument['parentDocument'] = new XML('<parentDocument classCode="DOCCLIN" moodCode="EVN"/>'); 

	    	// CONF-BC0556, CONF-BC0557, CONF-BC0558, CONF-BC0527
	    	relatedDocument['parentDocument']['id'] = $('parentDocumentId');

	    	// TODO: CONF-BC0128, CONF-BC0129   	
	}
} catch (e) {
    	var error = 'Error on "RelatedDocument": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try { 
	// 2.3. CDA LEVEL 1 NARRATIVE TEXT AND ATTACHMENTS

	var msgBody = msg['nonXMLBody'];  // inbound object

	if (getArrayOrXmlLength(msg['attachments']) > 0) {
		msgBody = {};
		msgBody['mediaType'] = 'text/plain';
		msgBody['content'] = 'Document has ' + getArrayOrXmlLength(msg['attachments']) + ' attachments.';
	}

	// CONF-BC0009, CONF-BC0010
	if (!msgBody || !msgBody['mediaType']) {
		storeOBIBError('obib.text.error', 'Document shall have exactly one text with mediaType defined');
	} else if (msgBody['mediaType'] === "text/plain") {
		// CONF-BC0011: text.mediaType=text/plain
		tmp['component']['nonXMLBody']['text'] = new XML('<text mediaType="' + msgBody['mediaType'] + '" representation="TXT">' 
			+ msgBody['content'] + ' \n(Sent at: ' + new Date() + ')</text>');
	} else if (msgBody['mediaType'] === "text/rtf" || msgBody['mediaType'] === "application/pdf") {
		// CONF-BC0012: text.mediaType=text/rtf, or application/pdf
		tmp['component']['nonXMLBody']['text'] = new XML('<text mediaType="' + msgBody['mediaType'] + '" ' 
											+ 'integrityCheck="' + msgBody['integrityCheck'] + '" ' 
											+ 'integrityCheckAlgorithm="' + msgBody['integrityCheckAlgorithm'] +'" >');
		tmp['component']['nonXMLBody']['text']['reference'] = new XML('<reference value="' + msgBody['content'] + '"/>');
	} else {
		storeOBIBError('obib.text.error', 'MediaType is not valid');
	}
} catch (e) {
    	var error = 'Error on "Document Body": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

try {
	var attachments = [];
	for (var i = 0; i < getArrayOrXmlLength(msg['attachments']); i++) {
		attachments.push(msg['attachments'][i]); // inbound object
	}

	if (attachments.length > 0) { // store the attachments in the channel map to be send by the connector
		logger.debug('Attachments being submitted: ' + attachments.length);
		channelMap.put('attachments', attachments);
	}
} catch (e) {
    	var error = 'Error on "Attachments": ' + e;
    	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

logger.debug("Document Validation");

/* Validate the CDA using the XML Schemas */
try {
	// build the XML with correct namespaces
	default xml namespace = "urn:hl7-org:v3";
	var msgValidate = new XML(tmp.toString());
	msgValidate.addNamespace(new Namespace('xsi','http://www.w3.org/2001/XMLSchema-instance'));

	// get the validator using the xml schemas
	var factory = new Packages.javax.xml.validation.SchemaFactory.newInstance('http://www.w3.org/2001/XMLSchema');
	var schemaLocation = new Packages.java.io.File($('custom-lib') + 'CDA_with_BcCdaExtensions/' + globalMap.get('CDASchema'));
	var schema = factory.newSchema(schemaLocation);
	var validator = schema.newValidator();

	// transform the xml into a stream
	var reader = new Packages.java.io.StringReader(msgValidate);
	var source = new Packages.javax.xml.transform.stream.StreamSource(reader);

	// validate the stream
	try {
		validator.validate(source);
	} catch(e) {
		var error = 'CDA XML validation errors: ' + e.message.replace('org.xml.sax.SAXParseException:','').replace(/("|')/g, '');
		logger.error(error); // log the error
    		storeOBIBError('obib.cda.validation.error', error);
	}
} catch(e) {
	var error = 'Error on "Document Validation": ' + e;
	logger.error(error); // log the error
    	storeOBIBError('obib.cda.build.error', error);
}

// check for errors in the cda building
var obibErrors = channelMap.get('obibErrors');
if (obibErrors != null) {
	var error = 'Error building CDA.';

	responseMap.put('responseMessage', createErrorMessage(error, obibErrors)); // create the error response
	responseMap.put('responseStatus', '200'); // return ok
}

logger.debug('Submit Document Response');

try {
	// Store the response to be returned by the "Source Connector"
	responseMap.put('responseMessage', handleResponseMessage(responseStatus, responseStatusMessage, msg, function() {
		// Ack details
		tmp['ackDetail']['ackId'] = msg['id']['@extension'].toString();
		tmp['ackDetail']['ackTime'] = parseCDADateTime(msg['creationTime']['@value'].toString());
		tmp['ackDetail']['messageId'] = msg['acknowledgement']['targetMessage']['id']['@extension'].toString();
		tmp['ackDetail']['status'] = msg['acknowledgement']['acknowledgementDetail']['@typeCode'].toString();

		// Payload
        	tmp['documentId'] = channelMap.get('documentId');

        	// Process the CDA Document in 'CDA Document Parser' channel, and return it in the response
        	var chResponse = router.routeMessage("CDA Document Parser", new XML(channelMap.get('document')));
		if (chResponse.status === SENT) {
			tmp['document'] = JSON.parse(chResponse.message);
			tmp['document']['cdaXML'] = channelMap.get('document');

			var attachments = channelMap.get('attachments');
			if (attachments !== null) {
				tmp['document']['attachments'] = attachments;
			}
		} else {
			logger.error('Error parsing CDA Document ' + chResponse.statusMessage);
		}

		// Message status
		if (tmp['ackDetail']['status'] === 'I') {
			tmp['status'] = "SUCCESS";

			logger.debug('MessageId ' + tmp['ackDetail']['messageId'] + ' was successfully sent.');
		} else {
			tmp['status'] = "ERROR";

			if (tmp['ackDetail']['status'] === 'E') {
				tmp['message'] = msg['acknowledgement']['acknowledgementDetail']['text'].toString();
			} else {
				tmp['message'] = 'Unespected error. Status code: ' + tmp['ackDetail']['status'];
			}

			logger.error('MessagId ' + tmp['ackDetail']['messageId'] + ' returned the error: ' + tmp['message'] + "'");
		}

		return tmp; // Return the transformed message to be converted on 'handleResponseMessage'
	}));
	responseMap.put('responseStatus', '200');
} catch(e) {
	logger.error("Error on 'Submit Document Response': " + e);

	responseMap.put('responseMessage', createErrorMessage(e.message, null));
	responseMap.put('responseStatus', '200');

	return ResponseFactory.getErrorResponse(responseMap.get('responseMessage'));
}

