com.mirth.connect.plugins.javascriptstep.script:
try {
	if (responseStatus === SENT) { // process only SENT messages
		// Document list, CONF-CDXMCR069
		for (var i = 0; i < getArrayOrXmlLength(msg['controlActProcess']['subject']); i++) { 
			var target = msg['controlActProcess']['subject'][i]['target']; // CONF-CDXMCR071

			// Process the CDA Document in 'CDA Document Parser' channel
			var chResponse = router.routeMessage("CDA Document Parser", new XML(target['text'].toString())); // CONF-CDXMCR082

			if (chResponse.status === SENT) {
				tmp['documents'][i] = JSON.parse(chResponse.message);
				tmp['documents'][i]['cdaXML'] = target['text'].toString();
			} else {
				throw 'Error parsing CDA Document ' + chResponse.statusMessage;
			}
		}
	}
} catch(e) {
	logger.error("Error on 'Build Response Document': " + e);
}

try {
	if (responseStatus === SENT) { // process only SENT messages
		if (getArrayOrXmlLength(msg['attachmentText']) > 0) { // Attachment list, CONF-CDXMCR103
			tmp['documents'][0]['attachments'] = [];
			for (var i = 0; i < getArrayOrXmlLength(msg['attachmentText']); i++) { 
				var attachmentText = msg['attachmentText'][i];

				var attachment = {};

				attachment['hash'] = attachmentText['@integrityCheck'].toString(); // CONF-CDXMCR105
				attachment['mediaType'] = attachmentText['@mediaType'].toString(); // CONF-CDXMCR107
				attachment['content'] = attachmentText.toString(); // CONF-CDXMCR108

				// Attachment only has name with it is embedeed in the document
				if (tmp['documents'][0]['nonXMLBody']['mediaType'] !== "text/plain") {
					attachment['reference'] = tmp['documents'][0]['nonXMLBody']['content'];
				}

				// Save the attachment in the first and only returned document
				tmp['documents'][0]['attachments'][i] = attachment; 
			}
		}
	}
} catch(e) {
	logger.error("Error on 'Get Attachments Response': " + e);
}

try {
	// Store the response to be returned by the "Source Connector"
	responseMap.put('responseMessage', handleResponseMessage(responseStatus, responseStatusMessage, msg, function() {
		// Ack details
		tmp['ackDetail']['ackId'] = msg['id']['@extension'].toString();
		tmp['ackDetail']['ackTime'] = parseCDADateTime(msg['creationTime']['@value'].toString());
		tmp['ackDetail']['messageId'] = msg['acknowledgement']['targetMessage']['id']['@extension'].toString();
		tmp['ackDetail']['status'] = msg['controlActProcess']['queryAck']['queryResponseCode']['@code'].toString();

		// Payload set by the previous transformers

		return handleQueryResultStatus(tmp, msg, 'documents'); // Return the transformed message to be converted on 'handleResponseMessage'
	}));
	responseMap.put('responseStatus', '200');
} catch(e) {
	logger.error("Error on 'Get Document Response': " + e);

	responseMap.put('responseMessage', createErrorMessage(e.message, null));
	responseMap.put('responseStatus', '200');
}

