				tmp['clinics'].push(JSON.parse(chResponse.message));
			} else {
				logger.error('Error parsing Clinic ' + chResponse.statusMessage);
			}
		}

		return handleQueryResultStatus(tmp, msg, 'clinics'); // Return the transformed message to be converted on 'handleResponseMessage'
	}));
	responseMap.put('responseStatus', '200');
