importPackage(Packages.ca.uvic.leadlab.cdxconnector);

try {
	var client = new WSClientDocument($('cdx.base.url'), $('username'), $('password'), $('certPath'), $('certPass'));

	var effectiveTime = null;
	if ($('effectiveTime_start').length() > 0 && $('effectiveTime_end').length() > 0) {
		effectiveTime = new DateRange(new Date($('effectiveTime_start')), true, new Date($('effectiveTime_end')), true);
	}

	var eventTime = null;
	if ($('eventTime_start').length() > 0 && $('eventTime_end').length() > 0) {
		eventTime = new DateRange(new Date($('eventTime_start')), true, new Date($('eventTime_end')), true);
	}

	// Call the WS method using CDX Connector
	var cdxResponse = client.getDistributionStatus($('locationId'), $('clinicId'), $('documentId'), effectiveTime, eventTime);

	// return the raw response to be processed by the 'response transformers'
	return ResponseFactory.getSentResponse(cdxResponse);
} catch (e) {
	logger.error("Error on 'Service Distribution Status': " + e);

	responseMap.put('responseMessage', createErrorMessage("Error on 'Service Distribution Status': " + e.message, null)); // create the error response
	responseMap.put('responseStatus', '200'); // return ok
}

com.mirth.connect.plugins.javascriptstep.script:
logger.debug('Distribution Status Response');

try {
	// Store the response to be returned by the "Source Connector"
	responseMap.put('responseMessage', handleResponseMessage(responseStatus, responseStatusMessage, msg, function() {
		// Ack details
		tmp['ackDetail']['ackId'] = msg['id']['@extension'].toString();
		tmp['ackDetail']['ackTime'] = parseCDADateTime(msg['creationTime']['@value'].toString());
		tmp['ackDetail']['messageId'] = msg['acknowledgement']['targetMessage']['id']['@extension'].toString();
		tmp['ackDetail']['status'] = msg['controlActProcess']['queryAck']['queryResponseCode']['@code'].toString();

		// Payload
		for (var i = 0; i < getArrayOrXmlLength(msg['controlActProcess']['subject']); i++) { // Document list
			var target = msg['controlActProcess']['subject'][i]['target'];
			var document = {};

			document['documentId'] = target['id']['@extension'].toString();

			document['template'] = {};
			document['template']['templateId'] = target['templateId']['@extension'].toString();
			document['template']['templateName'] = target['templateId']['@assigningAuthorityName'].toString();

			document['loinc'] = {};
			document['loinc']['loincCode'] = target['code']['@code'].toString();
			document['loinc']['displayName'] = target['code']['@displayName'].toString();

			document['effectiveTime'] = parseCDADateTime(target['effectiveTime']['@value'].toString());

			// Distribution Statuses
			document['distributionStatuses'] = [];
			for (var j = 0; j < getArrayOrXmlLength(target['informationRecipient']); j++) {
				var intendedRecipient = target['informationRecipient'][j]['intendedRecipient'];

				// Distribution Status
				var distributionStatus = {};
				distributionStatus['statusCode'] = intendedRecipient['receivedOrganization'].*::['distributionStatus'].*::['statusCode']['@code'].toString();
				distributionStatus['statusName'] = intendedRecipient['receivedOrganization'].*::['distributionStatus'].*::['statusCode']['@displayName'].toString();
				distributionStatus['statusTime'] = parseCDADateTime(intendedRecipient['receivedOrganization'].*::['distributionStatus'].*::['statusTime']['@value'].toString());

				// Primary Recipient
				distributionStatus['recipient'] = {};
				distributionStatus['recipient']['typeCode'] = target['informationRecipient'][j]['@typeCode'].toString();
				distributionStatus['recipient']['ids'] = [];
				for (var k = 0; k < getArrayOrXmlLength(intendedRecipient['id']); k++) {
					distributionStatus['recipient']['ids'][k] = parseCDAId(intendedRecipient['id'][k]);
				}
				distributionStatus['recipient']['name'] = parseCDAName(intendedRecipient['informationRecipient']['name']);

				// Received Organization
				distributionStatus['recipient']['receivedOrganization'] = {};
				distributionStatus['recipient']['receivedOrganization']['ids'] = [];
				for (var k = 0; k < getArrayOrXmlLength(intendedRecipient['receivedOrganization']['id']); k++) {
					distributionStatus['recipient']['receivedOrganization']['ids'][k] = parseCDAId(intendedRecipient['receivedOrganization']['id'][k]);
				}
				distributionStatus['recipient']['receivedOrganization']['name'] = intendedRecipient['receivedOrganization']['name'].toString();
				// TODO Received Organization Address?

				document['distributionStatuses'][j] = distributionStatus;
			}

			tmp['documents'][i] = document;
		}

		return handleQueryResultStatus(tmp, msg, 'documents'); // Return the transformed message to be converted on 'handleResponseMessage'
	}));
	responseMap.put('responseStatus', '200');
} catch(e) {
	logger.error("Error on 'Distribution Status Response': " + e);

	responseMap.put('responseMessage', createErrorMessage(e.message, null));
	responseMap.put('responseStatus', '200');
}

