	// escape apostrophe to insert the data into database
	var errorMsg = JSON.parse(connectorMessage.getEncodedData());
	var context = errorMsg['context'].replace(/\'/g, "''");
	var trace = errorMsg['trace'].replace(/\'/g, "''");

