		+ "', '" + trace + "', '" + $('obibVersion') + "', '" + $('connectorVersion') + "')";

	// Execute the insertion
	executeUpdate(insertString);
} catch(err) {
	logger.error('Error on "Store Error Message": ' + err);
}

