  		addr['city'] = city;
  	}
  	if (state) {
  		if (state.length == 2) {
  			state = 'CA-' + state;
  		}
  		addr['state'] = state; // CONF-BC0045
  	}
  	if (postalCode) {
  		addr['postalCode'] = postalCode;
  	}
  	if (country) {
  		addr['country'] = country; // CONF-BC0044
  	}
  	return addr;
}

/**
	Build a XML id tag

	@param {String} root - 
	@param {String} extension - 
	@return {Object} return the XML id tag
*/
function buildCDAId(root, extension) {
	if (extension != null && (""+extension).length > 0) {
		var id = new XML('<id/>');
	  	id['@extension'] = extension;
		if (root != null) {
		  	id['@root'] = root;
		  	// Get the authority name from the GlobalMap (loaded from database)
		  	var authName = globalMap.get(root);
		  	if (authName) {
		  		id['@assigningAuthorityName'] = authName;
		  	}
		}
	  	return id;
	}
	return new XML('<id nullFlavor="NA" />');
}

/**
	Build a XML name tag based on '4.2.1.1. Person Names'

	@param {String} use -
	@param {String} family -
	@param {String} given -
	@param {String} prefix -
	@param {String} suffix -
	@return {Object} return the XML name tag
*/
function buildCDAName(use, family, given, prefix, suffix) {
	var name = new XML("<name/>");
  	name['@use'] = use; // CONF-BC0038
  	name['family'] = family;
  	if (given instanceof Array) { // CONF-BC0031, CONF-BC0034
  		for (var i = 0; i < given.length; i++) {
  			name['given'][i] = given[i];
  		}
  	} else {
  		name['given'] = given;
  	}
  	if (prefix) { // CONF-BC0035
  		name['prefix'] = prefix;
  	}
  	if (suffix) { // CONF-BC0036
  		name['suffix'] = suffix;
  	}
  	return name;
}

/**
	Build a XML telecom tag

	@param {String} use - 'H' = home, 'W' = work, 'M' = mobile
	@param {String} type - 'tel' or 'email'
	@param {String} value - number of telefone or email address
	@return {Object} return the XML telecom tag
*/
function buildCDATelecom(use, type, value) {
	var telecom = new XML("<telecom/>");
	if (use && type && value) {
  		telecom['@use'] = use;
  		telecom['@value'] = type + ":" + value;
	}
  	return telecom;
}

/**
	Build a XML templateId tag

	@param {String} code - 
	@return {Object} return the XML template tag
*/
function buildCDATemplateId(code) {
	var templateId = new XML('<templateId/>');

	// Get the attributes from the GlobalMap (loaded from database)
  	var loinc_code = globalMap.get(code);

  	templateId['@root'] = loinc_code['template_id'];
  	templateId['@assigningAuthorityName'] = loinc_code['template_name'];

  	return templateId;
}

/**
	Format the date for the CDA format 'yyyyMMdd'

	@param {Date/String} date -
	@return {String} return the formated date
*/
function formatCDADate(date) {
	return DateUtil.convertDate("yyyy-MM-dd", "yyyyMMdd", date);
}

/**
	Format the date/time for the CDA format 'yyyyMMddHHmmZZZ'

	@param {Date/String} dateTime - 
	@return {String} return the formated dateTime
*/
function formatCDADateTime(dateTime) {
	return DateUtil.formatDate('yyyyMMddHHmmZZZ', new Date(dateTime));
}

/**
	Parse the CDA Address to JSON Object

	@param {String} cdaAddress - 
	@return {String} return the parsed Address object
*/
function parseCDAAddress(cdaAddress) {
	var addr = {};
	addr['use'] = cdaAddress['@use'].toString();
	// Try to get the street address from the body of the tag <addr> (Document format)
	var strtAddr = cdaAddress.toString().match(/<addr.*>(.+?)(?=<delimiter\/>)/); // return array of matchs
	if (strtAddr) {
		addr['streetAddress'] = strtAddr[1]; //.replace(/(<\/?[^>]+>)/gi, ''); // strip xml tags?
	}
	// Try to append the list of 'delimiters' (List clinics/providers format)
	if (getArrayOrXmlLength(cdaAddress['delimiter']) > 0) {
		for (var i = 0; i < getArrayOrXmlLength(cdaAddress['delimiter']); i++) {
			addr['streetAddress'] = (addr['streetAddress'] ? addr['streetAddress'] + ' ' : '') + cdaAddress['delimiter'][i].toString();
		}
		addr['streetAddress'] = addr['streetAddress'].trim(); // trim any whitespace from both side of the address
	}
	if (getArrayOrXmlLength(cdaAddress['city']) > 0) {
		addr['city'] = cdaAddress['city'].toString();
	}
	if (getArrayOrXmlLength(cdaAddress['province']) > 0) {
		addr['province'] = cdaAddress['state'].toString();
	}
	if (getArrayOrXmlLength(cdaAddress['postalCode']) > 0) {
		addr['postalCode'] = cdaAddress['postalCode'].toString();
	}
	if (getArrayOrXmlLength(cdaAddress['country']) > 0) {
		addr['country'] = cdaAddress['country'].toString();
	}
	return addr;
}

/**
	Parse the CDA Date/Time to Date object

	@param {String} cdaDateTime - 
	@return {String} return the Date object
*/
function parseCDADateTime(cdaDateTime) {
	if (!cdaDateTime || cdaDateTime.length == 0) {
		return null;
	}
	if (cdaDateTime.length <= 8) {
		return DateUtil.convertDate("yyyyMMdd", "yyyy-MM-dd", cdaDateTime);
	}
	return DateUtil.convertDate("yyyyMMddHHmmZZZ", "yyyy-MM-dd\'T\'HH:mm:ssZ", cdaDateTime);
}

/**
	Parse the CDA Date/Time to Date in DB format

	@param {String} cdaDateTime - 
	@return {String} return the Date in DB format
*/
function parseCDADateTimeToDB(cdaDateTime) {
	if (!cdaDateTime || cdaDateTime.length == 0) {
		return null;
	}
	if (cdaDateTime.length <= 8) {
		return DateUtil.convertDate("yyyyMMdd", "yyyy-MM-dd", cdaDateTime);
	}
	return DateUtil.convertDate("yyyyMMddHHmmZZZ", "yyyy-MM-dd hh:mm:ss", cdaDateTime);
}

/**
	Parse the CDA Id to JSON object

	@param {String} cdaID - 
	@return {String} return the parsed ID object
*/
function parseCDAId(cdaID) {
	var id = {};
	if (cdaID['@extension']) {
		id['code'] = cdaID['@extension'].toString();
	}
	if (cdaID['@root']) {
		id['type'] = cdaID['@root'].toString();
	}
	return id;
}

/**
	Parse the CDA Name to JSON object

	@param {String} cdaName - 
	@return {String} return the parsed Name object
*/
function parseCDAName(cdaName) {
	var name = {};
	name['use'] = cdaName['@use'].toString();
	name['family'] = cdaName['family'].toString();
	name['given'] = [];
	for (var i = 0; i < getArrayOrXmlLength(cdaName['given']); i++) {
		name['given'][i] = cdaName['given'][i].toString();
	}
	if (getArrayOrXmlLength(cdaName['prefix']) > 0) {
		name['prefix'] = cdaName['prefix'].toString();
	}
	if (getArrayOrXmlLength(cdaName['suffix']) > 0) {
		name['suffix'] = cdaName['suffix'].toString();
	}
	return name;
}

/**
	Parse the CDA Telecom to JSON object

	@param {String} telecom - 
	@return {String} return the parsed Telecom object
*/
function parseCDATelecom(cdaTelecom) {
	var telecom = {};
	telecom['use'] = cdaTelecom['@use'].toString();
	telecom['type'] = "tel"; 
	if (getArrayOrXmlLength(cdaTelecom['@value']) > 0) {
		var telParts = cdaTelecom['@value'].split(':');
		telecom['type'] = telParts[0];	
		telecom['value'] = telParts[1];
	}
	return telecom;
}

/**
	Build a Document Storage Message.

	@param {String} locationId - Location / Clinic Id
	@param {String} documentId - Document Id
	@param {String} messageType - DOCUMENT, RESPONSE or ATTACHMENT
	@param {String} content - Content of the message / document
	@param {String} connectorVersion - OBBI Connector version
	@return {String} return the Document Storage Message JSON object
*/
function buildDocumentStorageMessage(locationId, documentId, messageType, messageContent, connectorVersion) {
	var storageMsg = {};
	storageMsg['locationId'] = locationId;
	storageMsg['documentId'] = documentId;
	storageMsg['messageType'] = messageType;
	storageMsg['messageContent'] = messageContent;
	storageMsg['obibVersion'] = globalMap.get("obib.version");
	storageMsg['connectorVersion'] = connectorVersion;
	return storageMsg;
}

/**
	Create an Array of DocumentAttachment objects from a JSON object

	@param {Object[]} jsonAttachments - 
	@return {DocumentAttachment[]} return the Array of DocumentAttachment
*/
function createDocumentAttachmentArray(jsonAttachments) {
	var attachments = [];
	if (jsonAttachments != null) {
		for (var i = 0; i < jsonAttachments.length; i++) {
			var attachment = jsonAttachments[i];
			attachments.push(new Packages.ca.uvic.leadlab.cdxconnector.messages.submit.DocumentAttachment(
				attachment['mediaType'],
				attachment['content'],
				attachment['hash'],
				attachment['hashAlgorithm'],
				attachment['reference']));
		}
	}
	return attachments;
}

/**
	Get the DatabaseConnection object

	@return {DatabaseConnection} return the DatabaseConnection object
*/
function getDBConnection() {
	var dataSource = globalMap.get("hikari_OBIB_DB");
	if (dataSource == null) {
		dataSource = new Packages.com.zaxxer.hikari.HikariDataSource();
		dataSource.setDriverClassName(globalMap.get("obib.db.driver"));
		dataSource.setJdbcUrl(globalMap.get("obib.db.url"));
		dataSource.setUsername(globalMap.get("obib.db.username"));
		dataSource.setPassword(globalMap.get("obib.db.password"));
		dataSource.setConnectionTimeout(0);
		dataSource.setAutoCommit(false);
		dataSource.setMaximumPoolSize(10);
		dataSource.setMinimumIdle(0);
		//dataSource.setReadOnly(readOnly);
		//dataSource.setConnectionTestQuery("SELECT 1");
		globalMap.put("hikari_OBIB_DB", dataSource);
	}
	if (dataSource != null) {
		return dataSource.getConnection();
	} else {
		throw "Cannot create a new data source (connection pool).";
	}
}

/**
	Create JSON error message

	@param {String} error - Error message
	@param {Object} obibErrors - Associative Array of errors
	@return {String} return the JSON response
*/
function createErrorMessage(error, obibErrors) {
	// Filter cdxconnector messages
	if (error.includes('ca.uvic.leadlab.cdxconnector')) {
		error = error.split(/ca.uvic.leadlab.cdxconnector.*:/).pop();
	}

	return JSON.stringify({'obibVersion': globalMap.get("obib.version"), 'status': 'ERROR', 'message': error, 'obibErrors': obibErrors})
}

/**
	Create a new segment and insert it before the target segment.

	@param {String} name - The name of the segment to create.
	@param {String} segment - The existing segment that the new segment will be inserted before.
	@return {String} return The existing segment that the new segment will be inserted after.
*/
function createSegmentBefore(name, segment) {
	var msgObj = segment;
   	while (msgObj.parent() != undefined) { 
		msgObj = msgObj.parent(); 
	}
     msgObj.insertChildBefore(segment[0], new XML('<' + name + '></' + name + '>'));
   	return msgObj.child(segment[0].childIndex() - 1);
}

/**
	Evaluates a XPath expression

	@param {Object} xml - xml being evaluated
	@param {String} expression - expression to be evaluated
	@return {String} 'true' if the expression evaluates, otherwise 'false'
*/
function evaluateXPath(xml, expression) {
	// clean namespace
	var xmlStr = xml.toString().replace('xmlns="urn:hl7-org:v3"', '');

	// evaluate the xpath using Java xml API
	var input = new Packages.org.xml.sax.InputSource(new Packages.java.io.StringReader(xmlStr));
	var xpath = Packages.javax.xml.xpath.XPathFactory.newInstance().newXPath();
	return xpath.evaluate(expression, input, Packages.javax.xml.xpath.XPathConstants.BOOLEAN).toString();
}

/**
	Executes a query on the database and returns a CachedRowSet.

	@param {String} query - The query to be executed.
	@return {String} return The result of the query, as a CachedRowSet.
*/
function executeCachedQuery(query) {
	var connection = getDBConnection();
	if (connection != null) {
		var statement = null;
		try {
			statement = connection.createStatement();
            	var result = statement.executeQuery(query);
            	var crs = new MirthCachedRowSet();
            	crs.populate(result);
            	if (result != null) {
            		Packages.org.apache.commons.dbutils.DbUtils.closeQuietly(result);
            	}
            	return crs;
		} catch(err) {
			throw err;
        	} finally {
        		if (statement != null) {
				Packages.org.apache.commons.dbutils.DbUtils.closeQuietly(statement);
        		}
        		if (connection != null) {
        			Packages.org.apache.commons.dbutils.DbUtils.closeQuietly(connection);
        		}
		}
	} else {
		throw "Error: Cannot connect to the database";
	}
}

/**
	Executes an INSERT/UPDATE on the database and returns the row count.

	@param {String} query - The statement to be executed.
	@return {Integer} return A count of the number of updated rows.
*/
function executeUpdate(query) {
	var connection = getDBConnection();
	if (connection != null) {
		var statement = null;
		try {
			connection.setAutoCommit(true);
			statement = connection.createStatement();
			if (statement.execute(query)) {
				return -1;
			} else {
				return statement.getUpdateCount();
			}
		} catch (err) {
			throw err;
		} finally {
			if (statement != null) {
				Packages.org.apache.commons.dbutils.DbUtils.closeQuietly(statement);
        		}
        		if (connection != null) {
        			Packages.org.apache.commons.dbutils.DbUtils.closeQuietly(connection);
        		}
		}
	} else {
		throw "Error: Cannot connect to the database";
	}
}

/**
	Handle the result status from a query message.

	@param {Object} queryResult - query result ('tmp' variable)
	@param {Object} queryMessage - query message ('msg' variable)
	@param {String} collection - collection returned by the result
	@return {Object} return The queryResult object with status and message.
*/
function handleQueryResultStatus(queryResult, queryMessage, collection) {
	if (queryResult['ackDetail']['status'] === 'OK') {
		queryResult['status'] = "SUCCESS";
	} else if (queryResult['ackDetail']['status'] === 'NF') {
		queryResult['status'] = "SUCCESS";

		queryResult[collection] = []; // ensure the result collection is empty.
	} else {
		queryResult['status'] = "ERROR";

		if (queryResult['ackDetail']['status'] === 'QE') {
			queryResult['message'] = queryMessage['acknowledgement']['acknowledgementDetail']['text'].toString()				
		} else {
			queryResult['message'] = 'Unespected error. Ack response code: ' + queryResult['ackDetail']['status'];
		}

		logger.error('MessagId ' + queryResult['ackDetail']['messageId'] + ' returned the error: ' + queryResult['message'] + "'");
	}
	return tmp;
}

/**
	Handle the return message creation.

	@param {Object} responseStatus - 
	@param {Object} responseStatusMessage - 
	@param {Object} responseMessage - 
	@param {Function} createMessage - Callback function responsible for creating and return the result message.
	@return {String} return the stringify json message
*/
function handleResponseMessage(responseStatus, responseStatusMessage, responseMessage, createMessage) {
	if (responseStatus === SENT) { // process only SENT messages
		var responseMessage = createMessage(); // get the result message
		responseMessage['obibVersion'] = globalMap.get("obib.version"); // add versioning
		return JSON.stringify(responseMessage); // Convert the json to string and return it
	} else if (responseStatus === ERROR) {
		throw "Error Message: '" + responseStatusMessage.toString() 
			+ "'\n Original Message: '" + msg.toString() + "'";
	} else {
		throw "Not able to handle the response code: '" + responseStatus 
		+ "' \n Error Message: '" + responseStatusMessage.toString() 
		+ "'\n Original Message: '" + msg.toString() + "'";
	}
}

/**
	Store the error as an 'associative array' in the channel map.
	To retrieve the array of errors use 'channelMap.get('obibErrors')'

	@param {String} errorCode - the key of the associative array
	@param {String} errorMessage - the value of the associative array
*/
function storeOBIBError(errorCode, errorMessage) {
	var obibErrors = channelMap.get('obibErrors');
	if (obibErrors == null) {
		obibErrors = new Object();
	}
	obibErrors[errorCode] = errorMessage;
	channelMap.put('obibErrors', obibErrors);
}

