	tmp['participants'] = [];
	for (var i = 0; i < getArrayOrXmlLength(msg['participant']); i++) {
		var participant = {};

		participant['typeCode'] = msg['participant'][i]['@typeCode'].toString();
		participant['functionCode'] = msg['participant'][i]['functionCode']['@code'].toString();

		var associatedEntity = msg['participant'][i]['associatedEntity'];

		participant['ids'] = [];
		for (var j = 0; j < getArrayOrXmlLength(associatedEntity['id']); j++) {
			participant['ids'][j] = parseCDAId(associatedEntity['id'][j]);
		}

		if (getArrayOrXmlLength(associatedEntity['addr']) > 0) {
			participant['addresses'] = [];
			for (var j = 0; j < getArrayOrXmlLength(associatedEntity['addr']); j++) {
				participant['addresses'][j] = parseCDAAddress(associatedEntity['addr'][j]);
			}
		}

		if (getArrayOrXmlLength(associatedEntity['telecom']) > 0) {
			participant['telecoms'] = [];
			for (var j = 0; j < getArrayOrXmlLength(associatedEntity['telecom']); j++) {
				participant['telecoms'][j] = parseCDATelecom(associatedEntity['telecom'][j]);
			}
		}

		participant['name'] = parseCDAName(associatedEntity['associatedPerson']['name']);

		tmp['participants'][i] = participant;
	}
} catch(e) {
	logger.error("Error on 'Parse Participants': " + e);
}

try {
	// Orders
	tmp['orders'] = [];
	for (var i = 0; i < getArrayOrXmlLength(msg['inFulfillmentOf']); i++) {
		var order = {};

		var inFulfillmentOfOrder = msg['inFulfillmentOf'][i]['order'];

		order['ids'] = [];
		for (var j = 0; j < getArrayOrXmlLength(inFulfillmentOfOrder['id']); j++) {
			order['ids'][j] = parseCDAId(inFulfillmentOfOrder['id'][j]);
		}

		if (getArrayOrXmlLength(inFulfillmentOfOrder['code']) > 0) {
			order['statusCode'] = inFulfillmentOfOrder['code']['@code'].toString();
		}

		tmp['orders'][i] = order;
	}
} catch(e) {
	logger.error("Error on 'Parse Orders': " + e);
}

try {
	// ServiceEvents
	tmp['serviceEvents'] = [];
	for (var i = 0; i < getArrayOrXmlLength(msg['documentationOf']); i++) {
		var serviceEvent = {};

		var documentationOfServiceEvent = msg['documentationOf'][i]['serviceEvent'];

		if (getArrayOrXmlLength(documentationOfServiceEvent.*::['statusCode']) > 0) {
			serviceEvent['statusCode'] = documentationOfServiceEvent.*::['statusCode']['@code'].toString();
		}

		if (getArrayOrXmlLength(documentationOfServiceEvent['effectiveTime']) > 0) {
			serviceEvent['effectiveTime'] = parseCDADateTime(documentationOfServiceEvent['effectiveTime']['@value'].toString());
		}

		// Performers
		serviceEvent['performers'] = [];
		for (var j = 0; j < getArrayOrXmlLength(documentationOfServiceEvent['performer']); j++) {
			var performer = {};

			performer['typeCode'] = documentationOfServiceEvent['performer'][j]['@typeCode'].toString()

			var assignedEntity = documentationOfServiceEvent['performer'][j]['assignedEntity'];

			performer['code'] = assignedEntity['code'].toString();

			performer['ids'] = [];
			for (var k = 0; k < getArrayOrXmlLength(assignedEntity['id']); k++) {
				performer['ids'][k] = parseCDAId(assignedEntity['id'][k]);
			}

			if (getArrayOrXmlLength(assignedEntity['assignedPerson']) > 0) {
				performer['name'] = parseCDAName(assignedEntity['assignedPerson']['name']);
			}

			if (getArrayOrXmlLength(assignedEntity['addr']) > 0) {
				performer['addresses'] = [];
				for (var k = 0; k < getArrayOrXmlLength(assignedEntity['addr']); k++) {
					performer['addresses'][k] = parseCDAAddress(assignedEntity['addr'][k]);
				}
			}

			if (getArrayOrXmlLength(assignedEntity['telecom']) > 0) {
				performer['telecoms'] = [];
				for (var k = 0; k < getArrayOrXmlLength(assignedEntity['telecom']); k++) {
					performer['telecoms'][k] = parseCDATelecom(assignedEntity['telecom'][k]);
				}
			}

			serviceEvent['performers'][j] = performer;
		}

		tmp['serviceEvents'][i] = serviceEvent;
	}
} catch(e) {
	logger.error("Error on 'Parse ServiceEvents': " + e);
}

try {
	// ParentDocument
	tmp['parentDocuments'] = [];
	for (var i = 0; i < getArrayOrXmlLength(msg['relatedDocument']); i++) {
		var parentDocument = {};

		parentDocument['typeCode'] = msg['relatedDocument'][i]['@typeCode'].toString();

		var relatedParendDocument = msg['relatedDocument'][i]['parentDocument'];

		parentDocument['id'] = parseCDAId(relatedParendDocument['id']);

		if (getArrayOrXmlLength(relatedParendDocument['setId']) > 0) {
			parentDocument['setId'] = parseCDAId(relatedParendDocument['setId']);
		}

		if (getArrayOrXmlLength(relatedParendDocument['versionNumber']) > 0) {
			parentDocument['versionNumber'] = relatedParendDocument['versionNumber'].toString();
