		if (getArrayOrXmlLength(relatedParendDocument['text']) > 0) {
			parentDocument['mediaType'] = relatedParendDocument['text']['@mediaType'].toString();
		}

		tmp['parentDocuments'][i] = parentDocument;
	}
} catch(e) {
	logger.error("Error on 'Parse ParentDocuments': " + e);
}

