	// Text
	var text = msg['component']['nonXMLBody']['text'];

	tmp['nonXMLBody'] = {};
	tmp['nonXMLBody']['mediaType'] = text['@mediaType'].toString();

	if (text['@representation'] && text['@representation'].toString() === 'TXT') { 
		// Plain Text
		tmp['nonXMLBody']['content'] = text.toString();
