	tmp['providers'] = [];
	// Related Providers, CONF-CDXPR083
	for (var j = 0; j < getArrayOrXmlLength(assignedEntity['relatedTo']); j++) {
		var provider = {};

		var entity = assignedEntity['relatedTo'][j]['assignedEntity'];

		//if (entity['code']['@code'].toString() !== 'Provider') { // CONF-CDXPR084b
