		for (var k = 0; k < getArrayOrXmlLength(entity['id']); k++) {
			provider['ids'].push(parseCDAId(entity['id'][k]));
		}

		// Provider name, CONF-CDXPR088
		provider['name'] = parseCDAName(entity['name']);

		tmp['providers'].push(provider);
	}
} catch(e) {
	logger.error("Error on 'Parse Providers': " + e);
}

